import React, {useRef} from 'react'
import { Container, Row, Col } from 'reactstrap'
import { Link } from 'react-router-dom'
//import axios from 'axios'
//import "../styles/login.css"
import logo from '../images/res-logo.png'

const Login = () => {

  const emailRef = useRef()
  const passwordRef = useRef()

  const submitHandler = e => {
    e.preventDefault()
  }
/*
  const handleChange = (e) => {
    this.setState(
        {
          [e.target.name] : e.target.value
        }
    )
  }
  const onValidate = (e) =>
  { 
      if(this.state.Email==="" || this.state.Email===" " || this.state.Password==="" || this.state.Password===" ")
      {
          this.setState(
              {
                  EmptyCredentials:"Email and Password cannot be Empty!!"
              }
          )
          return false;
      }
  return true;
  }
*/
  return (
    <section>
        <div className='d-flex align-items-center justify-content-between'>
          <div className='logo'>
            <img src={logo} alt="logo" />
            <h5 className='text-center mt-2 '>Grubhub</h5>
          </div>
        </div>
        <Container>
        <Row>
            <Col lg='6' md='6' sm='12' className='m-auto text-center'>
            <form className='form mb-3 p-10' onSubmit={submitHandler}>
                <div className='form__group'>
                <input  type='email' 
                        placeholder='Email' 
                        required 
                        autoComplete='off' 
                        className='input__field mt-5' 
                        ref={emailRef}/>
                </div>
                <div className='form__group mt-4'>
                <input  type='password' 
                        placeholder='Password' 
                        required 
                        autoComplete='off' 
                        className='input__field' 
                        ref={passwordRef}/>
                </div>
                <button type="submit" className='login__btn mt-5 mb-3'>
                    Login
                </button>
            </form>
            <Link to='/register' className='link'>
                Don't have an account? Create an account
            </Link>
            </Col>
        </Row>
        </Container>
    </section>
  )
}

export default Login
