import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
import Header from './BuyerHeader'

const ViewRestaurant = () => {
  const { restaurantID } = useParams()
  //console.log(restaurantID)
  const [restaurant, setRestaurant] = useState([])
  const [sections, setSections] = useState([])

  useEffect( () => {
      axios.get(`http://localhost:8080/api/users/restaurant/${restaurantID}`, {
          headers: {
              'Content-Type': 'application/json',
              'Access-Control-Allow-Origin': 'http://localhost:3000',
          }
      }).then((response) => {
          setRestaurant(response.data);
      })
  }, [])

  useEffect(() => {
    axios.get("http://localhost:8080/api/users/sections")
          .then((response) => {
              setSections(response.data)
          })
          .catch((err) => console.log(err));
  },[])

  const getSectionItems = async (item) => {
    console.log(item.sectionID)
    setSection(item.sectionName)
    setShowSectionItems(true)
    setSectionID(item.sectionID)

    const sectionItems = {
      "sectionID": sectionID
    }

    try {
      await axios.get(`http://localhost:8080/api/users/section/${item.sectionID}`, sectionItems, {
          headers: {
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': 'http://localhost:3000',
          }
      }).then(async (response) => {
          setSectionItems(response.data)
        }) 
    } catch (err) {
        console.log("Error on Getting section Items")
      }
  }

  return (
    <div>
      <Header />
      <section> 
        {sections && sections.map((section) => {
          return (
            <Row lg='12' md='12' sm='12' xs='12' key={section.sectionID} className='mt-5 mb-0'>
              {section.sectionName}
            </Row>
          )
          })
        }
      </section>
    </div>
  )
}

export default ViewRestaurant
