import React, {useState, useRef} from 'react'
import { Modal, Button } from 'react-bootstrap';
import logo from '../images/res-logo.png'
import { useNavigate } from 'react-router-dom'
import axios from "axios";
import '../styles/header.css'

const Header = (props) => {
    const itemRef = useRef();

    const [displayed, setDisplay] = useState(false)
    const [show, setShow] = useState(false);
    const [showProfile, setShowProfile] = useState(false);
    const [sectionID, setSectionID] = useState(0);
    //const [restaurantID, setRestaurantID] = useState(0);
    const [itemName, setItemName] = useState('');
    const [itemDescription, setItemDescription] = useState('');
    const [itemPrice, setItemPrice] = useState(0);
    const [itemImage, setItemImage] = useState('');

    //console.log(props.user.restaurantID)
    const [firstName, setFirstName] = useState(props.user.firstName);
    const [lastName, setLastName] = useState(props.user.lastName);
    const [email, setEmail] = useState(props.user.email);
    const [phoneNumber, setPhoneNumber] = useState(props.user.phoneNumber);
    const [restaurantName, setRestaurantName] = useState(props.user.restaurantName);
    const [restaurantID, setRestaurantID] = useState(props.user.restaurantID);
    const [restaurantImage, setRestaurantImage] = useState(props.user.restaurantImage);
    const [cuisine, setCuisine] = useState(props.user.cuisine);
    const [profileImage, setProfileImage] = useState(props.user.setProfileImage);

    const navigate = useNavigate();

    const displayUser = () => {
        setDisplay(current => !current)
        //console.log(displayed)
    }
    const signOut = () => {
        navigate('/Ownerlogin')
    }

    const addItem = async (e) => {
        e.preventDefault();
        //setShow(true);
        const addedItem = {
            "itemName": itemName,
            "itemDescription": itemDescription,
            "itemPrice": itemPrice,
            "itemImage": itemImage,
            "sectionID": sectionID,
            "restaurantID": props.user.restaurantID
        }

        try {
          await axios.post("http://localhost:8080/api/users/section/item/addItem", addedItem , {
              headers: {
                  'Content-Type': 'application/json',
                  'Access-Control-Allow-Origin': 'http://localhost:3000',
              }
          }).then(async (response) => {
              console.log(response.data)
          })
        } catch (err) {
          console.log("Error on adding Item")
        }

        setShow(false);
        //window.location.reload();
    }

    const handleClose = () => {
        setShow(false)
        setShowProfile(false)
    }

  return (
    <header className='header d-flex align-items-center justify-content-between'>
        <div className='logo'>
            <img src={logo} alt="logo" />
            <h5 className='text-center'>Grubhub</h5>
        </div>

        <div className='menu d-flex align-items-center justify-content-between gap-5' >
            <button className='header__button' onClick={()=>setShow(true)}>Add Item</button>
            <a href='/Orders'>Orders</a>
        </div>

        <div className='nav_right d-flex align-items-center gap-4'>
            <span className='user' onClick={displayUser}>
                <i className="ri-user-3-line"></i>           
            </span>
        </div>

        <div className={displayed ? "user_info" : "user_none" }> 
            <p className='user_name'>Hi, Srinish</p>
            <p className='user_profile' onClick={()=>setShowProfile(true)} >Your Profile</p>
            <p className='sign_out' onClick={signOut}>Sign out</p>
        </div>

        <Modal className="setModal143" show={show} onHide={handleClose} centered={show}>
            <Modal.Header className="item2">
            <Modal.Title >Add Item</Modal.Title>
            </Modal.Header>
            <Modal.Body className="item3">
            <div className="form-group">
                Item Name <input type="text" id="sectionName" className="form-control" 
                onChange={(e) => setItemName(e.target.value)}  ref={itemRef} required />

                Item Description <input type="text" id="itemDescription" className="form-control" 
                onChange={(e) => setItemDescription(e.target.value)}  ref={itemRef} required />

                Item Price <input type="text" id="itemPrice" className="form-control" 
                onChange={(e) => setItemPrice(e.target.value)}  ref={itemRef} required />

                Item Image <input type="text" id="itemImage" className="form-control" 
                onChange={(e) => setItemImage(e.target.value)}  ref={itemRef} required />

                Section ID <input type="text" id="sectionID" className="form-control" 
                onChange={(e) => setSectionID(e.target.value)}  ref={itemRef} required />
            {/*
                Restaurant ID <input type="text" id="restaurantID" className="form-control" 
                onChange={(e) => setRestaurantID(e.target.value)}  ref={itemRef} required />
  */}
            </div>
            </Modal.Body>
            <Modal.Footer className="item2">
            <Button variant="primary" className="s-btn s-btn-primary u-block-xs--down s-col-sm-5 s-col-xs-12">
                <span className="s-btn-copy" onClick={addItem}>Save</span>
            </Button>
            <Button variant="secondary" onClick={handleClose} className="s-btn s-btn-primary u-block-xs--down s-col-sm-5 s-col-xs-12">
                <span className="s-btn-copy">Close</span>
            </Button>
            </Modal.Footer>
        </Modal>

        <Modal className="setModal143" show={showProfile} onHide={handleClose} centered={showProfile}>
            <Modal.Header className="item2">
            <Modal.Title >Your Profile</Modal.Title>
            </Modal.Header>
            <Modal.Body className="item3">
            <div className="form-group">
                First Name <input type="text" id="sectionName" className="form-control" 
                onChange={(e) => setItemName(e.target.value)}  ref={itemRef} required />

                Last Name <input type="text" id="itemDescription" className="form-control" 
                onChange={(e) => setItemDescription(e.target.value)}  ref={itemRef} required />

                Email <input type="text" id="itemPrice" className="form-control" 
                onChange={(e) => setItemPrice(e.target.value)}  ref={itemRef} required />

                Phone Number <input type="text" id="itemImage" className="form-control" 
                onChange={(e) => setItemImage(e.target.value)}  ref={itemRef} required />

                Restaurant Name <input type="text" id="sectionID" className="form-control" 
                onChange={(e) => setSectionID(e.target.value)}  ref={itemRef} required />

                Restaurant Image <input type="text" id="sectionID" className="form-control" 
                onChange={(e) => setSectionID(e.target.value)}  ref={itemRef} required />

                Cuisine <input type="text" id="sectionID" className="form-control" 
                onChange={(e) => setSectionID(e.target.value)}  ref={itemRef} required />

                Profile Image <input type="text" id="sectionID" className="form-control" 
                onChange={(e) => setSectionID(e.target.value)}  ref={itemRef} required />
            {/*
                Restaurant ID <input type="text" id="restaurantID" className="form-control" 
                onChange={(e) => setRestaurantID(e.target.value)}  ref={itemRef} required />
  */}
            </div>
            </Modal.Body>
            <Modal.Footer className="item2">
            <Button variant="primary" className="s-btn s-btn-primary u-block-xs--down s-col-sm-5 s-col-xs-12">
                <span className="s-btn-copy" onClick={addItem}>Save</span>
            </Button>
            <Button variant="secondary" onClick={handleClose} className="s-btn s-btn-primary u-block-xs--down s-col-sm-5 s-col-xs-12">
                <span className="s-btn-copy">Close</span>
            </Button>
            </Modal.Footer>
        </Modal>
        </header>
  )
}

export default Header
