import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
import Header from './BuyerHeader'
import { Col, Row } from 'reactstrap'
import ProductCard from './ProductCard'

const ViewRestaurant = () => {
  const { restaurantID } = useParams()
  //console.log(restaurantID)
  const [restaurant, setRestaurant] = useState([])
  const [sections, setSections] = useState([])
  const [sectionName, setSectionName] = useState('')
  const [sectionItems, setSectionItems] = useState([])
  const [sectionID, setSectionID] = useState(0)

  useEffect( () => {
      axios.get(`http://localhost:8080/api/users/restaurant/${restaurantID}`, {
          headers: {
              'Content-Type': 'application/json',
              'Access-Control-Allow-Origin': 'http://localhost:3000',
          }
      }).then((response) => {
          setRestaurant(response.data);
      })
  }, [])

  useEffect(() => {
    axios.get("http://localhost:8080/api/users/sections")
          .then((response) => {
              setSections(response.data)
              console.log(response.data)
          })
          .catch((err) => console.log(err));
  },[])

  useEffect(() => {
    axios.get("http://localhost:8080/api/users/section/items")
        .then((response) => {
          setSectionItems(response.data)
          console.log(response.data)
        })
        .catch((err) => console.log(err));
  },[])

 /* 
  useEffect(() => {
  //const getSectionItems = async (item) => {
    console.log(sectionID)
    setSectionName(sectionName)
    //setSectionID(item.sectionID)

    const sectionItems = {
      "sectionID": sectionID
    }

    try {
       axios.get(`http://localhost:8080/api/users/section/${sectionID}`, sectionItems, {
          headers: {
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': 'http://localhost:3000',
          }
      }).then(async (response) => {
          setSectionItems(response.data)
          console.log(response.data)
        }) 
    } catch (err) {
        console.log("Error on Getting section Items")
      }
  }, [sectionName])
*/
  return (
    <div>
      <Header />
      <section> 
        {sections && sections.map((section) => {
          if(section.restaurantID.restaurantID == restaurantID) {
            return (
              <Row>
                <Col lg='12'  key={section.sectionID}  className='rest_sections mt-5 m-auto'>
                  {section.sectionName}
                </Col>  

                {sectionItems && sectionItems.map((item) => {
                    return (
                      <Col lg='3' md='4' sm='6' xs='6' key={item.itemID} className='mt-5'>
                        <ProductCard item={item} />
                      </Col>
                    )
                  }  
                )}

              </Row>
            )
          }
          })
        }
      </section>
    </div>
  )
}

export default ViewRestaurant
