import React, {useRef, useState, useEffect} from 'react'
import Footer from '../components/Footer'
import Header from '../components/Header'
import PageTitle from '../components/PageTitle'
import axios from "axios";
//import cookie from 'react-cookies';
import { Container, Row, Col } from 'reactstrap'
import { Modal, Button } from 'react-bootstrap';
import "../styles/home.css"

const Home = () => {
  const sectionRef = useRef();

  const [section, setSection] = useState('ALL');
  const [show, setShow] = useState(false);
  const [sectionName, setSectionName] = useState('');
  const [showSectionName, setShowSectionName] = useState('');
  const [menu, setMenu] = useState([]);
  const [data, setData] = useState([])

  useEffect(() => {
    axios.get("http://localhost:8080/api/users/sections")
          .then((response) => {
            //const data = JSON.stringify(response.data);
            //const array = JSON.parse(data).array;
            setData(response.data)
            console.log(response.data.restaurantID);
            //data.map((item, index) => menu.push(item.sectionName)) 
            //data.map((item) => console.log(item.sectionName))
          })
          .catch((err) => console.log(err));
  },[])

  const addSection = async (e) => {
    e.preventDefault();
    setShow(true);
    //let userid = cookie.load('cookie2');
    //console.log("User id is " + userid);
    const SectionName = sectionName;
    console.log("IN Add  section.......section name...ui  " + sectionName);
    const addedSection = {}
    if(sectionName != ''){
      addedSection = {
        "sectionName": SectionName,
        "restaurantID": 51
      }
    }

    //axios.defaults.withCredentials = true;
    try {
      await axios.post("http://localhost:8080/api/users/sections/addSection", addedSection , {
          headers: {
              'Content-Type': 'application/json',
              'Access-Control-Allow-Origin': 'http://localhost:3002',
          }
      }).then(async (response) => {
          setShowSectionName(response.data.sectionName)
      })
    } catch (err) {
      console.log("Error on adding Section")
    }

    setShow(false);

  }
  const handleClose = () => {
    setShow(false)
  }

  return (
    <PageTitle title="Home">
          <Header />

          <section>
            <Container>
              <Row>
                <Col lg='12' className='text-center'>
                  <h2>Popular Foods</h2>
                </Col>

                <Col lg='12'>
                  <div className='food__category d-flex align-items-center justify-content-center gap-4'>
                    <button className={`all__btn ${section === 'ALL' ? 'foodBtnActive' : ''}`}
                      onClick={()=>setSection('ALL')}>All</button>

                    {data && data.map((item, key) => {
                      return (
                        <button className={`d-flex align-items-center gap-2 ${section === item.sectionName ? 'foodBtnActive' : ''}`} 
                        key={key} onClick={()=>setSection(item.sectionName)}>{item.sectionName}</button>
                      )
                    })}

                    <button className={`d-flex align-items-center gap-2 ${true ? 'add__section': ''}` }
                    onClick={()=>setShow(true)}>Add Section</button>
                  </div>
                </Col>
              </Row>
              <Modal className="setModal143" show={show} onHide={handleClose} centered={show}>
                <Modal.Header className="item2">
                  <Modal.Title >Add Section</Modal.Title>
                </Modal.Header>
                <Modal.Body className="item3">
                  <div className="form-group">
                    Section Name <input type="text" id="sectionName" className="form-control" 
                    onChange={(e) => setSectionName(e.target.value)} value={sectionName} ref={sectionRef} required />
                  </div>
                </Modal.Body>
                <Modal.Footer className="item2">
                  <Button variant="primary" className="s-btn s-btn-primary u-block-xs--down s-col-sm-5 s-col-xs-12">
                    <span className="s-btn-copy" onClick={addSection}>Save</span>
                  </Button>
                  <Button variant="secondary" onClick={handleClose} className="s-btn s-btn-primary u-block-xs--down s-col-sm-5 s-col-xs-12">
                    <span className="s-btn-copy">Close</span>
                  </Button>
                </Modal.Footer>
              </Modal>
            </Container>
          </section>

          <Footer />
    </PageTitle>
  )
}

export default Home
