import { useRef, useState, useEffect } from 'react';
import Home from './Home';
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios';
import '../styles/register.css'
import logo from '../images/res-logo.png'

const Login = () => {

    const userRef = useRef();
    const errRef = useRef();

    const [email, setEmail] = useState('');
    const [pwd, setPwd] = useState('');

    const [EmptyCredentials, setEmptyCredentials] = useState('');
    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(() => {
        userRef.current.focus();
    }, [])

    useEffect(() => {
        setErrMsg('');
    }, [email, pwd])

    const navigate = useNavigate();

    const onValidate = (e) => { 
        if(email === "" || email === " " || pwd === "" || pwd === " ") {
            setEmptyCredentials("Email and Password cannot be Empty!!")
            return false;
        }
        return true;
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        let isValid = onValidate();
        if(isValid){
          const logindata = {
            "Email" : email,
            "password" : pwd
          }

          try {
              const response = await axios.post("http://localhost:8080/api/users/login", logindata, {
                  headers: {
                    'Content-Type': 'application/json',
                    'Access-Control-Allow-Origin': 'http://localhost:3000',
                  }
              }).then(async (response) => {
                  console.log("Status Code : ====> ",response.status);
                  if(response.status === 200){
                      navigate('/home')
                  }
                  console.log(response.data);
              }
              )
          } catch (err) {
              if (!err?.response) {
                  setErrMsg('No Server Response');
              }else if(response.status === 202) {
                  setErrMsg('Incorrect Credentials')
              }else {
                  setErrMsg("Error in login")
              }

              errRef.current.focus();
          }
        }
    }

    return (
        <>
            {success ? (
                <Home />
            ) : (
                <section>
                  <div className='d-flex align-items-center justify-content-between'>
                      <div className='logo'>
                          <img src={logo} alt="logo" />
                          <h5 className='text-center mt-2 '>Grubhub</h5>
                      </div>
                  </div>
                  <div className='formSection'>
                      <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
                      <h1 className='headerName'>Sign In</h1>
                      <form onSubmit={handleSubmit}>
                          <label htmlFor="email">Email:</label>
                          <input
                              type="email"
                              id="email"
                              ref={userRef}
                              autoComplete="off"
                              onChange={(e) => setEmail(e.target.value)}
                              value={email}
                              required
                          />

                          <label htmlFor="password">Password:</label>
                          <input
                              type="password"
                              id="password"
                              onChange={(e) => setPwd(e.target.value)}
                              value={pwd}
                              required
                          />
                          <button className='signInButton'>Sign In</button>
                      </form>
                      <p>
                          Need an Account?<br />
                          <span className="line">
                            <Link to='/register'>
                              Sign Up
                            </Link>
                          </span>
                      </p>
                  </div>
                </section>
            )}
        </>
    )
}

export default Login
