import React, { useRef } from 'react'

import logo from '../images/res-logo.png'
import {NavLink, Link } from 'react-router-dom'

import '../styles/header.css'

const nav_links =[
  {
    display: 'Home',
    path: '/home'
  },
  {
    display: 'Foods',
    path: '/foods'
  },
  {
    display: 'Cart',
    path: '/cart'
  },
  {
    display: 'Contact',
    path: '/contact'
  }
]


const Header = () => {

  const menuRef = useRef(null);
  const headerRef = useRef(null);

  const toggleMenu = () => menuRef.current.classList.toggle('show__menu');
/*
  useEffect(()=> {
    window.addEventListener('scroll',()=>{
      if(document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
        headerRef.current.classList.add('header__shrink');
      }

      else {
        headerRef.current.classList.remove('header__shrink');
      }
    })

    return ()=> window.removeEventListener('scroll')

  },[])
*/
  return (
    <header className='header' ref={headerRef}>

      <div className='nav_wrapper d-flex align-items-center justify-content-between'>
        <div className='logo'>
          <img src={logo} alt="logo" />
          <h5 className='text-center'>Grubhub</h5>
        </div>

          <div className='menu d-flex align-items-center justify-content-between gap-5' >

            <a href='/addSection' className='lin'>Add Section</a>
            <a href='/updateSection'>Update Section</a>
            <a href='/addItem'>Add Item</a>
            <a href='/updateItem'>Update Item</a>

          </div>

        <div className='nav_right d-flex align-items-center gap-4'>
            <span className='user'>
              <Link to='/login'>
                <i className="ri-user-3-line"></i>
              </Link>
            </span>
        </div>

      </div>
  </header>
  )
}

export default Header

