import React, { useRef } from 'react'
import logo from '../images/res-logo.png'
import {NavLink, Link } from 'react-router-dom'
import '../styles/header.css'

const Header = () => {
    const menuRef = useRef(null);
    const toggleMenu = () => menuRef.current.classList.toggle

    const nav_links =[
        {
            display: 'Add Section',
            path: '/addSection'
        },
        {
            display: 'Update Section',
            path: '/updateSection'
        },
        {
            display: 'Add Item',
            path: '/addItem'
        },
        {
            display: 'Update Item',
            path: '/updateItem'
        }
    ]
    return (
        <header className='header d-flex align-items-center justify-content-between'>
            <div className='logo_name d-flex align-items-center '>
                <img src={logo} alt="logo" className='logo' />
                <h5 className='rest_name'>GRUBHUB</h5>
            </div>

            <div className='navigation'>
                <div className='menu d-flex align-items-center justify-content-between gap-5' >
                    {
                        nav_links.map((item, index)=>(
                            <NavLink 
                                onClick={toggleMenu}
                                to={item.path} 
                                key={index}
                                className={(navClass) => navClass.isActive ? "active_menu" : ""}
                            >
                                {item.display}
                            </NavLink>
                        ))
                    }
                </div>
            </div>

            <div className='nav_right d-flex align-items-center gap-4'>
                <span className='user'>
                    <i className="ri-user-3-line"></i>
                </span>
            </div>
        </header>
    )
    }


    export default Header
