  const addToCart = (item) => {
    const cartCopy = cartItems.slice();

    const index = cartCopy.findIndex((product) => item.id === product.id);

    if (index === -1) {
      cartCopy.push({ ...item, count: 1 });
    } else {
      const pr = cartCopy[index];
      cartCopy[index] = { ...pr, count: pr.count + 1 };
    }

    setCartItems(cartCopy);
  };

  const incrementCount = (item) => {
    /*setQuantity(quantity => quantity + 1)*/
    const cartCopy = cartItems.slice();

    const index = cartCopy.findIndex((product) => item.id === product.id);

    const pr = cartCopy[index];
    cartCopy[index] = { ...pr, count: pr.count + 1 };

    setCartItems(cartCopy);
  }

  const decrementCount = (item) => {
    /*if(quantity > 1) {
        setQuantity(quantity => quantity - 1)
    }*/
    const cartCopy = cartItems.slice();

    const index = cartCopy.findIndex((product) => item.id === product.id);

    const pr = cartCopy[index];
    cartCopy[index] = { ...pr, count: pr.count - 1 };

    setCartItems(cartCopy);
  }

  const removeFromCart = (item) => {
    const cartCopy = cartItems.filter((product) => item.id !== product.id);
    setCartItems(cartCopy);
  }

