import React, { useEffect, useState } from 'react'
import axios from 'axios'
import '../styles/order.css'

const Order = () => {

  const restaurantID = sessionStorage.getItem('restaurantID')
  const restaurantName = sessionStorage.getItem('restaurantName')
  const restaurantImage = sessionStorage.getItem('restaurantImage')

  const [newOrders, setNewOrders] = useState([])

  const getOrdersByRestaurantId = async () => {
    try {
      await axios.get(`http://localhost:8080/api/users/owner/order/${restaurantID}`,  {
          headers: {
            'Content-Type': 'application/json',
            'Access-Control-Allow-Origin': 'http://localhost:3000',
          }
      }).then(async (response) => {
          setNewOrders(response.data)
          console.log(response.data)
        })
    } catch (err) {
        console.log("Error on Getting Restaurant Orders")
      }
  }

  useEffect(() => {
    getOrdersByRestaurantId()
  },[])

  return (
    <div>
      <h1 className='order_heading'>New Orders</h1>

      {newOrders && newOrders.map((order, key) => {
        return (
          <div className='order p-2' key={key}>
            <img src={restaurantImage} alt="restaurant" ></img>
            <div>
              <div className='d-flex justify-content-between align-content-center'>
                <div className='d-flex p-2'>
                  <h2 className='name'>{order.firstName}</h2>
                  <h2>{order.lastName}</h2>
                </div>
                <h2 className='price p-2'>${order.totalPrice}</h2>
              </div>
              <div className='address'>
                <h3>{order.address}</h3>
              </div>
              <div>
                {order.orderItems && order.orderItems.map((orderItem, key) => {
                  return (
                    <div key={key} className='d-flex'>
                      <h5>{orderItem.quantity}</h5>
                      <p>{orderItem.itemName}</p>
                    </div>
                  )
                })}
              </div>
            </div>
          </div>
        )
      })}
    </div>
  )
}

export default Order
